import React, { useState } from "react";
import styles from "../styles";
import { chevronDown } from "../assets";
import { Modal } from "antd";
import tokenList from "../tokenList";

const Exchange = () => {
  const [tokenOneAmount, setTokenOneAmount] = useState("");
